
public class GreatestNumm {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=100;
		int b=300;
		int c=200;
		if(a>b && a>c) {
			System.out.println(a);
		}
		else if(b>a && b>c) {
			System.out.println(b);
		}
		else if(c>a && c>b) {
			System.out.println(c);
		}
	}
		

}
